query.sql:
DECLARE
  CURSOR c_impresiones IS
    SELECT imp.data_salida
      FROM mc_datos_impresion imp
     WHERE imp.secuencia_impresion = (SELECT MAX(qry.secuencia_impresion)
                                        FROM mc_datos_impresion qry
                                       WHERE qry.secuencia_impresion = NVL(:p_secuencia_impresion,qry.secuencia_impresion))
  ORDER BY imp.secuencia_impresion DESC, imp.numero_linea ASC;
BEGIN
  dbms_output.enable(NULL);

  FOR i IN c_impresiones LOOP
    dbms_output.put_line('<p>'||i.data_salida||'</p>');
  END LOOP;
END;
