query.sql:
DECLARE
  CURSOR C_Delta (Cv_Solicitud  Sg_Deltas.Solicitud_Changepoint%TYPE) IS
    SELECT Del.Id Delta_Id,
           Del.Solicitud_Changepoint,
           (SELECT Per.Nombre_Empleado||' '||Per.Apellido_Empleado FROM Sg_Personal Per WHERE Per.Usuario_Id = Del.Usuario_Id_Creadopor) AS Creado_Por,
           Del.Fecha_Creacion,
           (SELECT Cli.Nombre_Cliente From Sg_Clientes Cli WHERE Cli.Cliente_Id = Del.Cliente_Id) AS Nombre_Cliente,
           Del.Nombre_Apk,
           NVL(Del.Descripcion,'NINGUNA') Descripcion
      FROM Sg_Deltas Del
     WHERE Del.Solicitud_Changepoint = UPPER(C_Delta.Cv_Solicitud);

  Lr_Deltas      C_Delta%ROWTYPE;
  Lb_Encontro    BOOLEAN;
  NOT_FOUND      EXCEPTION;
  OTROS_ERRORES  EXCEPTION;
BEGIN
  IF C_Delta%ISOPEN THEN
    CLOSE C_Delta;
  END IF; -- IF C_Delta%ISOPEN THEN

  OPEN C_Delta (:P_SOLICITUD);
  FETCH C_Delta INTO Lr_Deltas;
  Lb_Encontro := C_Delta%NOTFOUND;
  CLOSE C_Delta;

  IF Lb_Encontro THEN
    dbms_output.put_line('<b>No se Encontro el Delta Especificado</b>');
    RAISE NOT_FOUND;
  ELSE
    dbms_output.put_line('<b>Delta a Eliminar</b>: '  ||Lr_Deltas.Solicitud_Changepoint||'<p />'||
                         '<b>Creado Por</b>: '        ||Lr_Deltas.Creado_Por           ||'<p />'||
                         '<b>Fecha de Creacion</b>: ' ||Lr_Deltas.Fecha_Creacion       ||'<p />');

    dbms_output.put_line('<b>Nombre del Cliente</b>: '||Lr_Deltas.Nombre_Cliente       ||'<p />'||
                         '<b>Nombre del APK</b>: '    ||Lr_Deltas.Nombre_Apk           ||'<p />'||
                         '<b>Descripcion</b>: '       ||Lr_Deltas.Descripcion          ||'<br />');

    BEGIN
      dbms_output.put_line('<b>Iniciando Eliminación de SG_DOCUMENTOS</b><p />');
      DELETE FROM Sg_Documentos WHERE Delta_Id = Lr_Deltas.Delta_Id;
      dbms_output.put_line('<b>Total de Registros Eliminados</b>: '||SQL%ROWCOUNT||'<p />');

      dbms_output.put_line('<b>Iniciando Eliminación de SG_EVENTOS</b><p />');
      DELETE FROM Sg_Eventos WHERE Delta_Id = Lr_Deltas.Delta_Id;
      dbms_output.put_line('<b>Total de Registros Eliminados</b>: '||SQL%ROWCOUNT||'<p />');

      dbms_output.put_line('<b>Iniciando Eliminación de SG_ARCHIVOS_EXTERNOS</b><p />');
      DELETE FROM Sg_Archivos_Externos WHERE Delta_Id = Lr_Deltas.Delta_Id;
      dbms_output.put_line('<b>Total de Registros Eliminados</b>: '||SQL%ROWCOUNT||'<p />');

      dbms_output.put_line('<b>Iniciando Eliminación de SG_DELTAS</b><p />');
      DELETE FROM Sg_Deltas WHERE Id = Lr_Deltas.Delta_Id;
      dbms_output.put_line('<b>Total de Registros Eliminados</b>: '||SQL%ROWCOUNT||'<p />');

      COMMIT;
      dbms_output.put_line('<b>REGISTROS ELIMINADOS SATISFACTORIAMENTE</b>');
    EXCEPTION
      WHEN OTHERS THEN
        dbms_output.put_line('<b>PROBLEMAS AL INTENTAR ELIMINAR REGISTROS</b>: '||SQLERRM);
        RAISE OTROS_ERRORES;
    END;
  END IF; -- IF Lb_Encontro THEN
EXCEPTION
  WHEN NOT_FOUND THEN
    ROLLBACK;
  WHEN OTROS_ERRORES THEN
    ROLLBACK;
END;

DECLARE
  CURSOR C_Delta (Cv_Solicitud  Sg_Deltas.Solicitud_Changepoint%TYPE) IS
    SELECT Del.Id Delta_Id,
           Del.Descripcion,
           (SELECT Per.Codigo_Base_De_Datos FROM Sg_Personal Per WHERE Per.Usuario_Id = Del.Usuario_Id_Creadopor) Creado_Por
      FROM Sg_Deltas Del
     WHERE Del.Solicitud_Changepoint = UPPER(C_Delta.Cv_Solicitud);

  CURSOR C_Objetos (Cn_DeltaID  Sg_Archivos_Externos.Delta_Id%TYPE) IS
    SELECT ArcExt.Id_Tipo_Obj,
           ArcExt.NombreSolo Objeto
      FROM Sg_Archivos_Externos ArcExt
     WHERE ArcExt.Delta_Id = C_Objetos.Cn_DeltaID
  ORDER BY ArcExt.Orden ASC;

  CURSOR C_Documentos (Cn_DeltaID  Sg_Documentos.Delta_Id%TYPE) IS
    SELECT Doc.Nombre_Del_Documento
      FROM Sg_Documentos Doc
     WHERE Doc.Delta_Id = C_Documentos.Cn_DeltaID
  ORDER BY Doc.Documento_Id ASC;

  Lr_Delta       C_Delta%ROWTYPE;
  Lv_DDLNodo     VARCHAR2(32000);
  Lv_DDLAgen     VARCHAR2(32000);
  Lv_ObjNodo     VARCHAR2(32000);
  Lv_ObjAgen     VARCHAR2(32000);
  Lv_FMBNodo     VARCHAR2(32000);
  Lv_FMBAgen     VARCHAR2(32000);
  Lv_RDFNodo     VARCHAR2(32000);
  Lv_RDFAgen     VARCHAR2(32000);
  Lv_Documentos  VARCHAR2(32000);
  Lb_Objetos     CLOB;
BEGIN
  IF C_Delta%ISOPEN THEN
    CLOSE C_Delta;
  END IF;
  OPEN C_Delta (Cv_Solicitud => :P_SOLICITUD);
  FETCH C_Delta INTO Lr_Delta;
  CLOSE C_Delta;

  DBMS_OUTPUT.PUT_LINE('<p>'||SUBSTR(Lr_Delta.Descripcion,1,9)||'</p>'||
                       '<p>'||SUBSTR(Lr_Delta.Descripcion,11) ||'</p><br />');

  FOR Obj IN C_Objetos (Cn_DeltaID => Lr_Delta.Delta_Id) LOOP

    IF Obj.Id_Tipo_Obj = 24 THEN                   -- DDL NODO
      Lv_DDLNodo := Lv_DDLNodo || '<p>  ' || Obj.Objeto || '</p>';
    ELSIF Obj.Id_Tipo_Obj = 23 THEN                -- DDL AGEN
      Lv_DDLAgen := Lv_DDLAgen || '<p>  ' || Obj.Objeto || '</p>';
    ELSIF Obj.Id_Tipo_Obj IN (9,11,13,15,22) THEN  -- OBJETOS BD NODO
      Lv_ObjNodo := Lv_ObjNodo || '<p>  ' || Obj.Objeto || ' IDVERSION0000 </p>'||
                                  '<p> Aplicado en: ' || NULL                ||'</p>'||
                                  '<p> Responsable: ' || Lr_Delta.Creado_Por ||'</p>';
    ELSIF Obj.Id_Tipo_Obj IN (10,12,14,17,21) THEN -- OBJETOS BD AGEN
      Lv_ObjAgen := Lv_ObjAgen || '<p>  ' || Obj.Objeto || ' IDVERSION0000 </p>'||
                                  '<p> Aplicado en: ' || NULL                ||'</p>'||
                                  '<p> Responsable: ' || Lr_Delta.Creado_Por ||'</p>';
    ELSIF Obj.Id_Tipo_Obj = 1 THEN                 -- FORMA NODO
      Lv_FMBNodo := Lv_FMBNodo || '<p>  ' || Obj.Objeto || '   IDVERSION0000</p>';
    ELSIF Obj.Id_Tipo_Obj = 2 THEN                 -- FORMA AGEN
      Lv_FMBAgen := Lv_FMBAgen || '<p>  ' || Obj.Objeto || '   IDVERSION0000</p>';
    ELSIF Obj.Id_Tipo_Obj = 3 THEN                 -- REPORTE NODO
      Lv_RDFNodo := Lv_RDFNodo || '<p>  ' || Obj.Objeto || '   IDVERSION0000</p>';
    ELSIF Obj.Id_Tipo_Obj = 4 THEN                 -- REPORTE AGEN
      Lv_RDFAgen := Lv_RDFAgen || '<p>  ' || Obj.Objeto || '   IDVERSION0000</p>';
    END IF; -- IF Obj.Id_Tipo_Obj = 24 THEN

  END LOOP; -- FOR Obj IN C_Objetos (Cn_DeltaID => Lr_Delta.Delta_Id) LOOP

  FOR Doc IN C_Documentos (Cn_DeltaID => Lr_Delta.Delta_Id) LOOP
    Lv_Documentos := '<p>' || Doc.Nombre_Del_Documento || '</p>';
  END LOOP; -- FOR Doc IN C_Documentos (Cn_DeltaID => Lr_Delta.Delta_Id) LOOP

  SELECT DECODE(Lv_DDLNodo, NULL, NULL,'<p>SCRIPTS:<p>'    ||
                                          '<p>Nodo:<p>'    ||
                                       Lv_DDLNodo)         ||
         DECODE(Lv_DDLAgen, NULL, NULL,   '<p>Agencia:</p>'||
                                       Lv_DDLAgen)         ||
         DECODE(Lv_ObjNodo, NULL, NULL,'<p>OBJETOS:</p>'   ||
                                          '<p>Nodo:<p>'    ||
                                       Lv_ObjNodo)         ||
         DECODE(Lv_ObjAgen, NULL, NULL,   '<p>Agencia:</p>'||
                                       Lv_ObjAgen)
    INTO Lb_Objetos
    FROM dual;

  --MENERROR(Lb_Objetos);
END;

DECLARE
  CURSOR C_Delta (Cv_Solicitud  Sg_Deltas.Solicitud_Changepoint%TYPE) IS
    SELECT Del.Id Delta_Id,
           Del.Solicitud_Changepoint,
