  IF Lb_Encontro THEN
    dbms_output.put_line('<b>No se Encontro el Delta Especificado</b>');
    RAISE NOT_FOUND;
  ELSE
    dbms_output.put_line('<b>Delta</b>: '             ||Lr_Deltas.Solicitud_Changepoint||'<p />'||
