    BEGIN
      dbms_output.put_line('<b>Iniciando Eliminación de SG_EVENTOS</b><p />');
      DELETE FROM Sg_Eventos WHERE Delta_Id = Lr_Deltas.Delta_Id;
      dbms_output.put_line('<b>Total de Registros de Eventos Eliminados</b>: '||SQL%ROWCOUNT||'<p />');

      COMMIT;
      dbms_output.put_line('<b>REGISTROS ELIMINADOS SATISFACTORIAMENTE</b>');
    EXCEPTION
      WHEN OTHERS THEN
        dbms_output.put_line('<b>PROBLEMAS AL INTENTAR ELIMINAR REGISTROS DE EVENTOS</b>: '||SQLERRM);
        RAISE OTROS_ERRORES;
    END;
  END IF; -- IF Lb_Encontro THEN
EXCEPTION
  WHEN NOT_FOUND THEN
    ROLLBACK;
  WHEN OTROS_ERRORES THEN
    ROLLBACK;
END;

DECLARE
  Lv_NuevoNombre     Sg_Deltas.Solicitud_Changepoint%TYPE;
  Lv_NombreAnterior  Sg_Deltas.Solicitud_Changepoint%TYPE;
BEGIN
  Lv_NuevoNombre    := :P_NuevoNombre;
  Lv_NombreAnterior := :P_NombreAnterior;

  UPDATE Sg_Deltas
     SET Solicitud_Changepoint = Lv_NuevoNombre,
         Nombre_Apk            = Lv_NuevoNombre || '.apk'
   WHERE Solicitud_Changepoint = Lv_NombreAnterior;

  IF SQL%NOTFOUND THEN
    dbms_output.put_line('La Solicitud <b>'||Lv_NombreAnterior||'</b> no existe, Verifique!');
  ELSE
    COMMIT;
    dbms_output.put_line('La Solicitud <b>'||Lv_NombreAnterior||'</b> cambio al nombre <b>'||Lv_NuevoNombre||'</b>');
  END IF; -- IF SQL%NOTFOUND THEN
END;
